import React from 'react';
import logo from './images/header-logo.svg';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';

import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup';

// const [isPopupOpen, setPopupOpen] = React.useState(false);

const App = () => {

  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);

  const handleEditAvatarClick = () => {
    setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
  }

  const handleEditProfileClick = () => {
    setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
  }

  const handleAddPlaceClick = () => {
    setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
  }

  const closeAllPopups = () => {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
  }

  return (
    <>
      <div className="root">
        <Header logo={logo} />
        <Main
          onEditAvatarClick={handleEditAvatarClick}
          onEditProfileClick={handleEditProfileClick}
          onAddPlaceClick={handleAddPlaceClick}
        />
        <Footer />
        <PopupWithForm name="EditAvatar" title="Обновить аватар" popupSize="medium" isOpen={isEditAvatarPopupOpen}>
          <div className="popup__input-container">
            <input data-field-name="avatar" id="popup__avatar-link" type="url" className="popup__text-field" placeholder="Ссылка на картинку" required />
            <span id="popup__avatar-link-error" className="popup__input-error"></span>
          </div>
        </PopupWithForm>
        <PopupWithForm name="EditProfile" title="Редактировать профиль" popupSize="large" isOpen={isEditProfilePopupOpen}>
          <div className="popup__input-container">
            <input data-field-name="name" id="popup__user-name" type="text" className="popup__text-field" autoFocus placeholder="Имя" minLength="2" maxLength="40" required />
            <span id="popup__user-name-error" className="popup__input-error"></span>
          </div>
          <div className="popup__input-container">
            <input data-field-name="about" id="popup__user-about" type="text" className="popup__text-field" placeholder="О себе" minLength="2" maxLength="200" required />
            <span id="popup__user-about-error" className="popup__input-error"></span>
          </div>
        </PopupWithForm>
        <PopupWithForm name="AddPhoto" title="Новое место" popupSize="large" isOpen={isAddPlacePopupOpen}>
          <div className="popup__input-container">
            <input data-field-name="name" id="popup__card-name" type="text" className="popup__text-field" autoFocus placeholder="Название" minLength="1" maxLength="30" required />
            <span id="popup__card-name-error" className="popup__input-error"></span>
          </div>
          <div className="popup__input-container">
            <input data-field-name="link" id="popup__card-link" type="url" className="popup__text-field" placeholder="Ссылка на картинку" required />
            <span id="popup__card-link-error" className="popup__input-error"></span>
          </div>
        </PopupWithForm>
        <PopupWithForm name="AddPhoto" title="Вы уверены?" popupSize="small" isOpen={isEditAvatarPopupOpen} />
      </div>
      <div id="popupIncreasePhoto" className="popup popup_min-transparent">
        <div className="popup__image-container">
          <button type="button" className="popup__close-button"></button>
          <img className="popup__image" src="#" alt="#" />
          <figure className="popup__image-figure"></figure>
        </div>
      </div>
      <template id="photo-card-template">
        <li className="photo-card">
          <button type="button" className="photo-card__trash-icon" value=""></button>
          <img className="photo-card__image" src="#" alt="#" />
          <div className="photo-card__footer">
            <h3 className="photo-card__title"></h3>
              <div className="photo-card__like-container">
                <button type="button" className="photo-card__like-icon"></button>
                <p className="photo-card__like-counter">0</p>
              </div>
          </div>
        </li>
      </template>

      <form id="popupConfirm" className="popup popup_half-transparent">
        <div className="popup__form-container popup__form-container_small">
          <button type="reset" className="popup__close-button" value=""></button>
          <h2 className="popup__title">Вы уверены?</h2>
          <button type="submit" className="popup__save-button">Да</button>
        </div>
      </form>
    </>
  );
}

export default App;
