import React from 'react';
import PopupWithForm from './PopupWithForm.js';

const EditAvatarPopup = (props) => {

  const avatarRef = React.useRef();

  function handleSubmit(event) {
    event.preventDefault();

    console.log(avatarRef.current);

    props.onUpdateAvatar({
      avatar: event.target.value
    });
  }

  return(
    <PopupWithForm name="EditAvatar" title="Обновить аватар" popupSize="medium" isOpen={props.isOpen} onClose={props.onClose}>
      <div className="popup__input-container">
        <input data-field-name="avatar" id="popup__avatar-link" type="url" className="popup__text-field" placeholder="Ссылка на картинку" required />
        <span id="popup__avatar-link-error" className="popup__input-error" />
      </div>
      <button type="submit" onClick={handleSubmit} className="popup__save-button popup__save-button_disabled">Сохранить</button>
    </PopupWithForm>
  );
}

export default EditAvatarPopup;
