import React from 'react';

const PopupWithForm = (props) => {

  return (
    <form id={`popup${props.name}`} className={`popup popup_half-transparent ${props.isOpen ? 'popup_opened' : ''}`} noValidate>
      <div className={`popup__form-container popup__form-container_${props.popupSize}`}>
        <button type="reset" className="popup__close-button" value=""></button>
        <h2 className="popup__title">{props.title}</h2>
        {props.children}
        <button type="submit" className="popup__save-button popup__save-button_disabled" disabled>Создать</button>
      </div>
    </form>
  );
}

export default PopupWithForm;
