import React from 'react';
import appApi from '../utils/Api.js';
import Card from './Card.js';

import CurrentUserContext from '../contexts/CurrentUserContext.js';

const Main = (props) => {

  const currentUser = React.useContext(CurrentUserContext);

  const [userAvatar, setUserAvatar] = React.useState('');
  const [userName, setUserName] = React.useState('');
  const [userDescription, setUserDescription] = React.useState('');

  const [cards, setCards] = React.useState([]);

  React.useEffect( () => {
    Promise.all([appApi.getUserInfo(), appApi.getInitialCards()]).then(
      ([userData, cards]) => {
        setUserAvatar(userData.avatar);
        setUserName(userData.name);
        setUserDescription(userData.about);
        setCards(cards);
      });
    }, []);

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__avatar-container">
          <img className="profile__avatar" alt="аватар" src={userAvatar} />
          <button onClick={props.onEditAvatarClick} type="button" className="profile__avatar-edit-button" />
        </div>
          <div className="profile__info">
            <div className="profile__info-row">
              <p className="profile__user-name">{userName}</p>
              <button onClick={props.onEditProfileClick} type="button" className="profile__edit-button" />
            </div>
            <p className="profile__user-about">{userDescription}</p>
          </div>
        <button onClick={props.onAddPlaceClick} type="button" className="profile__add-button" />
      </section>
      <section className="photo-grid">
        <ul className="photo-cards">
          {cards.map(card => <Card onCardClick={props.onCardClick} key={card._id} {...card} />)}
        </ul>
      </section>
    </main>
  );
}

export default Main;
